import React, { Component } from "react";

class MountingExample extends Component {
  // Step 1: Constructor
  constructor(props) {
    super(props);
    this.state = { count: 0 };
    console.log("Constructor: Component is being initialized.");
  }

  // Step 2: Get Derived State From Props (rarely used)
  static getDerivedStateFromProps(props, state) {
    console.log("getDerivedStateFromProps: Synchronizing state with props.");
    return null; // Return updated state or null to leave state unchanged
  }

  // Step 3: Render Method
  render() {
    console.log("Render: Component is being rendered.");
    return (
      <div>
        <h1>React Mounting Lifecycle</h1>
        <p>Count: {this.state.count}</p>
        <button onClick={() => this.setState({ count: this.state.count + 1 })}>
          Increment
        </button>
      </div>
    );
  }

  // Step 4: Component Did Mount
  componentDidMount() {
    console.log(
      "componentDidMount: Component has been inserted into the DOM."
    );
    // Example: Fetching data or setting up subscriptions
    setTimeout(() => {
      console.log("Simulating an async operation.");
      this.setState({ count: 10 });
    }, 2000);
  }
}

export default MountingExample;
